import React, { useState } from 'react';
import EventModal from '../components/EventModal';

const Events = () => {
  const [filter, setFilter] = useState('all');
  const [selectedEvent, setSelectedEvent] = useState(null);
  const [isModalOpen, setIsModalOpen] = useState(false);

  // Event details data
  const eventDetails = {
    'throwball': {
      name: 'Throwball',
      description: 'Fast-paced team sport similar to volleyball, popular in South Asia.',
      timing: '9:00 AM - 12:00 PM',
      venue: 'Main Court A',
      players: [
        { player1: 'Team Alpha', player2: 'Team Beta' },
        { player1: 'Team Gamma', player2: 'Team Delta' },
        { player1: 'Team Epsilon', player2: 'Team Zeta' }
      ]
    },
    'volleyball': {
      name: 'Volleyball',
      description: 'Classic team sport where two teams hit a ball over a net.',
      timing: '2:00 PM - 5:00 PM',
      venue: 'Main Court B',
      players: [
        { player1: 'Champions', player2: 'Warriors' },
        { player1: 'Thunder', player2: 'Lightning' },
        { player1: 'Eagles', player2: 'Hawks' }
      ]
    },
    'basketball': {
      name: 'Basketball',
      description: 'Fast-paced game where teams try to score by shooting a ball through a hoop.',
      timing: '9:30 AM - 1:00 PM',
      venue: 'Basketball Court 1 & 2',
      players: [
        { player1: 'Lions', player2: 'Tigers' },
        { player1: 'Bears', player2: 'Wolves' },
        { player1: 'Sharks', player2: 'Dolphins' }
