import React, { useState, useEffect } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';

const Header = () => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const navigate = useNavigate();
  const location = useLocation();

  useEffect(() => {
    // Any future auth logic can go here
  }, []);

  const isActive = (path) => location.pathname === path;

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  return (
    <header>
      <div className="container">
        <nav className="navbar">
          <Link to="/login" className="logo"><img src="./images/logos/Sample Logo 3D 1.png" alt="Rotary with Unit Logo" width="100" height="50" /></Link>
          <div className={`hamburger-menu ${isMenuOpen ? 'active' : ''}`} onClick={toggleMenu}>
            <span></span>
            <span></span>
            <span></span>
          </div>
          <ul className={`nav-links ${isMenuOpen ? 'active' : ''}`}>
            <li><button className="nav-button"><Link to="/" className={isActive('/') ? 'active' : ''}>Home</Link></button></li>
            <li><button className="nav-button"><Link to="/about" className={isActive('/about') ? 'active' : ''}>About</Link></button></li>
            <li><button className="nav-button"><Link to="/events" className={isActive('/events') ? 'active' : ''}>Events</Link></button></li>
            <li><button className="nav-button"><Link to="/points" className={`nav-link ${location.pathname === '/' ? 'active' : ''}`}>Points Table</Link></button></li>
            <li><button className="nav-button"><Link to="/schedule" className={isActive('/schedule') ? 'active' : ''}>Schedule</Link></button></li>
            <li><button className="nav-button"><Link to="/images" className={isActive('/images') ? 'active' : ''}>Photos</Link></button></li>
            <li><button className="nav-button"><Link to="/registration" className={isActive('/registration') ? 'active' : ''}>Registration</Link></button></li>
            <li><button className="nav-button"><Link to="/contact" className={isActive('/contact') ? 'active' : ''}>Contact</Link></button></li>

          </ul>
        </nav>
      </div>
    </header>
  );
};

export default Header;
