import React, { useState, useEffect } from 'react';
import { Link, useLocation} from 'react-router-dom';

const Header = () => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [setIsAuthenticated] = useState(false);
  const location = useLocation();

  useEffect(() => {
    const authStatus = localStorage.getItem('isAdminAuthenticated') === 'true';
    setIsAuthenticated(authStatus);
  }, []);

  const isActive = (path) => location.pathname === path;

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  return (
    <header>
      <div className="container">
        <nav className="navbar">
          <Link to="/login" className="logo"><img src="./images/logos/Sample Logo 3D 1.png" alt="Rotary with Unit Logo" width="100" height="50" /></Link>
          <div className="hamburger-menu" onClick={toggleMenu}>
            <span></span>
            <span></span>
            <span></span>
          </div>
          <ul className={`nav-links ${isMenuOpen ? 'active' : ''}`}>
            <li><Link to="/" className={isActive('/') ? 'active' : ''}>Home</Link></li>
            <li><Link to="/about" className={isActive('/about') ? 'active' : ''}>About</Link></li>
            <li><Link to="/events" className={isActive('/events') ? 'active' : ''}>Events</Link></li>
            <li><Link to="/points" className={`nav-link ${location.pathname === '/' ? 'active' : ''}`}>Points Table</Link></li>
            <li><Link to="/schedule" className={isActive('/schedule') ? 'active' : ''}>Schedule</Link></li>
            <li><Link to="/images" className={isActive('/images') ? 'active' : ''}>Photos</Link></li>
            <li><Link to="/registration" className={isActive('/registration') ? 'active' : ''}>Registration</Link></li>
            <li><Link to="/contact" className={isActive('/contact') ? 'active' : ''}>Contact</Link></li>
          </ul>
        </nav>
      </div>
    </header>
  );
};

export default Header;
