import { useEffect, useState } from "react";

export default function PublicView() {
  const [teams, setTeams] = useState([]);

  useEffect(() => {
    fetch("http://localhost:5000/api/teams")
      .then(res => res.json())
      .then(data =>
        setTeams(data.sort((a, b) => b.points - a.points))
      );
  }, []);

  return (
    <div className="points-table-container">
      <h2>üèÜ Leaderboard</h2>

      <table>
        <thead>
          <tr>
            <th>Team</th>
            <th>G</th>
            <th>S</th>
            <th>B</th>
            <th>Bonus</th>
            <th>Points</th>
          </tr>
        </thead>
        <tbody>
          {teams.map(team => (
            <tr key={team._id}>
              <td>{team.name}</td>
              <td>{team.gold}</td>
              <td>{team.silver}</td>
              <td>{team.bronze}</td>
              <td>+{team.bonus}</td>
              <td>{team.points}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
