import { useEffect, useState } from "react";

export default function AdminPage() {
  const [teams, setTeams] = useState([]);
  const [teamName, setTeamName] = useState("");
  const [events, setEvents] = useState([]);
  const [selectedEvent, setSelectedEvent] = useState("");
  const [player1, setPlayer1] = useState("");
  const [player2, setPlayer2] = useState("");
  const [editingPlayer, setEditingPlayer] = useState(null);
  const [editingIndex, setEditingIndex] = useState(-1);
  const [photoFile, setPhotoFile] = useState(null);

  const loadTeams = () => {
    fetch("http://localhost:5000/api/teams")
      .then(res => res.json())
      .then(setTeams);
  };

  useEffect(loadTeams, []);

  const loadEvents = () => {
    fetch("http://localhost:5000/api/events")
      .then(res => res.json())
      .then(setEvents);
  };

  useEffect(() => {
    loadEvents();
  }, []);

  const addPoint = (teamId, type) => {
    fetch("http://localhost:5000/api/admin/add-point", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ teamId, type })
    }).then(loadTeams);
  };

  const addTeam = () => {
    fetch("http://localhost:5000/api/admin/add-team", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ name: teamName })
    }).then(() => {
      setTeamName("");
      loadTeams();
    });
  };

  const addPlayerPair = () => {
    if (!selectedEvent) {
      alert("Please select an event");
      return;
    }
    if (!player1.trim()) {
      alert("Player 1 name is required");
      return;
    }

    const event = events.find(e => e._id === selectedEvent);
    if (!event) return;

    const updatedPlayers = [...event.players, { player1, player2: player2 || "-" }];
    
    fetch(`http://localhost:5000/api/admin/events/${selectedEvent}/players`, {
      method: "PUT",
      headers: {
        "Content-Type": "application/json",
        "x-admin-access": process.env.REACT_APP_ADMIN_ACCESS_KEY || "admin123"
      },
      body: JSON.stringify({ players: updatedPlayers })
    })
    .then(res => res.json())
    .then(data => {
      if (data.success !== false) {
        setPlayer1("");
        setPlayer2("");
        loadEvents();
        alert("Player pair added successfully");
      } else {
        alert(data.message || "Error adding player pair");
      }
    })
    .catch(err => {
      console.error("Error:", err);
      alert("Error adding player pair");
    });
  };

  const startEditingPlayer = (player, index) => {
    setPlayer1(player.player1);
    setPlayer2(player.player2);
    setEditingPlayer(player);
    setEditingIndex(index);
  };

  const updatePlayerPair = () => {
    if (!selectedEvent) {
      alert("Please select an event");
      return;
    }
    if (!player1.trim()) {
      alert("Player 1 name is required");
      return;
    }

    const event = events.find(e => e._id === selectedEvent);
    if (!event) return;

    const updatedPlayers = [...event.players];
    updatedPlayers[editingIndex] = { player1, player2: player2 || "-" };
    
    fetch(`http://localhost:5000/api/admin/events/${selectedEvent}/players`, {
      method: "PUT",
      headers: {
        "Content-Type": "application/json",
        "x-admin-access": process.env.REACT_APP_ADMIN_ACCESS_KEY || "admin123"
      },
      body: JSON.stringify({ players: updatedPlayers })
    })
    .then(res => res.json())
    .then(data => {
      if (data.success !== false) {
        setPlayer1("");
        setPlayer2("");
        setEditingPlayer(null);
        setEditingIndex(-1);
        loadEvents();
        alert("Player pair updated successfully");
      } else {
        alert(data.message || "Error updating player pair");
      }
    })
    .catch(err => {
      console.error("Error:", err);
      alert("Error updating player pair");
    });
  };

  const deletePlayerPair = (index) => {
    if (!selectedEvent) {
      alert("Please select an event");
      return;
    }

    if (!window.confirm("Are you sure you want to delete this player pair?")) {
      return;
    }

    const event = events.find(e => e._id === selectedEvent);
    if (!event) return;

    const updatedPlayers = event.players.filter((_, i) => i !== index);
    
    fetch(`http://localhost:5000/api/admin/events/${selectedEvent}/players`, {
      method: "PUT",
      headers: {
        "Content-Type": "application/json",
        "x-admin-access": process.env.REACT_APP_ADMIN_ACCESS_KEY || "admin123"
      },
      body: JSON.stringify({ players: updatedPlayers })
    })
    .then(res => res.json())
    .then(data => {
      if (data.success !== false) {
        loadEvents();
        alert("Player pair deleted successfully");
      } else {
        alert(data.message || "Error deleting player pair");
      }
    })
    .catch(err => {
      console.error("Error:", err);
      alert("Error deleting player pair");
    });
  };

  const uploadLivePhoto = () => {
    if (!photoFile) {
      alert("Please select a photo to upload");
      return;
    }

    const formData = new FormData();
    formData.append("photo", photoFile);

    fetch("http://localhost:5000/api/admin/upload-photo", {
      method: "POST",
      headers: {
        "x-admin-access": process.env.REACT_APP_ADMIN_ACCESS_KEY || "admin123"
      },
      body: formData
    })
    .then(res => res.json())
    .then(data => {
      if (data.success) {
        setPhotoFile(null);
        alert("Photo uploaded successfully");
      } else {
        alert(data.message || "Error uploading photo");
      }
    })
    .catch(err => {
      console.error("Error:", err);
      alert("Error uploading photo");
    });
  };

  return (
    <div className="container">
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '20px' }}>
        <h2>‚öôÔ∏è Admin Panel</h2>
        <button 
          onClick={() => {
            // Remove auth token and trigger a window reload to update all components
            localStorage.removeItem('isAdminAuthenticated');
            window.location.href = '/login';
          }}
          style={{
            backgroundColor: '#d32f2f',
            color: 'white',
            border: 'none',
            padding: '8px 16px',
            borderRadius: '4px',
            cursor: 'pointer'
          }}
        >
          Logout
        </button>
      </div>

      <input
        placeholder="New Team Name"
        value={teamName}
        onChange={e => setTeamName(e.target.value)}
      />
      <button onClick={addTeam}>Add Team</button>

      {teams.map(team => (
        <div key={team._id} className="card">
          <h3>{team.name}</h3>

          <button onClick={() => addPoint(team._id, "gold")}>+ Gold</button>
          <button onClick={() => addPoint(team._id, "silver")}>+ Silver</button>
          <button onClick={() => addPoint(team._id, "bronze")}>+ Bronze</button>
          <button onClick={() => addPoint(team._id, "bonus")}>+ Bonus</button>
        </div>
      ))}

      {/* Player Management Section */}
      <div className="section" style={{ marginTop: '40px', padding: '20px', border: '1px solid #ddd', borderRadius: '8px' }}>
        <h3>üë• Player Management</h3>
        <div style={{ marginBottom: '20px' }}>
          <label>Select Event: </label>
          <select 
            value={selectedEvent} 
            onChange={e => {
              setSelectedEvent(e.target.value);
              setEditingPlayer(null);
              setEditingIndex(-1);
              setPlayer1("");
              setPlayer2("");
            }}
          >
            <option value="">-- Select Event --</option>
            {events.map(event => (
              <option key={event._id} value={event._id}>{event.title}</option>
            ))}
          </select>
        </div>

        {selectedEvent && (
          <>
            <div style={{ marginBottom: '20px' }}>
              <input
                placeholder="Player 1 Name"
                value={player1}
                onChange={e => setPlayer1(e.target.value)}
                style={{ marginRight: '10px', padding: '8px', width: '200px' }}
              />
              <input
                placeholder="Player 2 Name (optional)"
                value={player2}
                onChange={e => setPlayer2(e.target.value)}
                style={{ marginRight: '10px', padding: '8px', width: '200px' }}
              />
              {editingPlayer ? (
                <>
                  <button 
                    onClick={updatePlayerPair}
                    style={{
                      backgroundColor: '#4CAF50',
                      color: 'white',
                      border: 'none',
                      padding: '8px 16px',
                      borderRadius: '4px',
                      cursor: 'pointer',
                      marginRight: '10px'
                    }}
                  >
                    Update Player
                  </button>
                  <button 
                    onClick={() => {
                      setPlayer1("");
                      setPlayer2("");
                      setEditingPlayer(null);
                      setEditingIndex(-1);
                    }}
                    style={{
                      backgroundColor: '#f44336',
                      color: 'white',
                      border: 'none',
                      padding: '8px 16px',
                      borderRadius: '4px',
                      cursor: 'pointer'
                    }}
                  >
                    Cancel
                  </button>
                </>
              ) : (
                <button 
                  onClick={addPlayerPair}
                  style={{
                    backgroundColor: '#2196F3',
                    color: 'white',
                    border: 'none',
                    padding: '8px 16px',
                    borderRadius: '4px',
                    cursor: 'pointer'
                  }}
                >
                  Add Player Pair
                </button>
              )}
            </div>

            <div>
              <h4>Current Players:</h4>
              {events.find(e => e._id === selectedEvent)?.players.map((player, index) => (
                <div key={index} className="card" style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', padding: '10px', margin: '5px 0' }}>
                  <div>
                    <strong>Player 1:</strong> {player.player1} | <strong>Player 2:</strong> {player.player2}
                  </div>
                  <div>
                    <button 
                      onClick={() => startEditingPlayer(player, index)}
                      style={{
                        backgroundColor: '#FF9800',
                        color: 'white',
                        border: 'none',
                        padding: '4px 8px',
                        borderRadius: '4px',
                        cursor: 'pointer',
                        marginRight: '5px'
                      }}
                    >
                      Edit
                    </button>
                    <button 
                      onClick={() => deletePlayerPair(index)}
                      style={{
                        backgroundColor: '#f44336',
                        color: 'white',
                        border: 'none',
                        padding: '4px 8px',
                        borderRadius: '4px',
                        cursor: 'pointer'
                      }}
                    >
                      Delete
                    </button>
                  </div>
                </div>
              ))}
              {(!events.find(e => e._id === selectedEvent)?.players.length || events.find(e => e._id === selectedEvent)?.players.length === 0) && (
                <p>No players added yet.</p>
              )}
            </div>
          </>
        )}
      </div>

      {/* Live Photo Upload Section */}
      <div className="section" style={{ marginTop: '40px', padding: '20px', border: '1px solid #ddd', borderRadius: '8px' }}>
        <h3>üì∏ Upload Live Photos</h3>
        <div style={{ marginBottom: '20px' }}>
          <input
            type="file"
            accept="image/*"
            onChange={(e) => setPhotoFile(e.target.files[0])}
            style={{ marginBottom: '10px' }}
          />
          <br />
          <button 
            onClick={uploadLivePhoto}
            style={{
              backgroundColor: '#4CAF50',
              color: 'white',
              border: 'none',
              padding: '10px 20px',
              borderRadius: '4px',
              cursor: 'pointer'
            }}
          >
            Upload Photo
          </button>
        </div>
      </div>
    </div>
  );
}
